INSERT INTO `ms_dataset` VALUES (23, 'Indeks Keamanan Informasi Berdasarkan Tingkat Kematangan di Jawa Barat', 'indeks-keamanan-informasi-berdasarkan-tingkat-kematangan-di-jawa-barat-1623227269', 'Indeks Keamanan Informasi Berdasarkan Tingkat Kematangan', 7, 17, 'Level', '1', '2016', '[\"CSV\",\"Excel\",\"API\"]', '<p><span style=\"color: #616161; font-family: Lato; font-size: 14px; white-space: pre-wrap;\">Dataset ini berisi data indeks keamanan informasi berdasarkan tingkat kematangan Di Provinsi Jawa Barat tahun 2019. Dataset terkait topik Infrastruktur ini dihasilkan oleh Dinas Komunikasi Dan Informatika yang dikeluarkan dalam periode 1 tahun sekali. Penjelasan mengenai variabel di dalam Dataset ini: * provinsi : menyatakan lingkup data berasal dari wilayah Provinsi Jawa Barat. * kode_kabupaten_kota : menyatakan kode dari tiap-tiap kota dan kabupaten sesuai dengan penamaan BPS. * nama_kabupaten_kota : menyatakan lingkup data berasal dari tiap-tiap kota dan kabupaten di Provinsi Jawa Barat. * tingkat_kematangan : menyatakan tingkatan dari indeks keamanan informasi. * tingkat : menyatakan tingkat dari klasifikasi tingkat kematangan indeks keamanan informasi. * satuan : menyatakan satuan dari pengukuran indeks keamanan informasi dalam level. * tahun : menyatakan tahun produksi data.</span></p>', 'Kabupaten', 'Kabupaten Karawang', 'Tahunan', '1', '2021-06-02 10:14:43', 45, '1', '2021-06-01 08:53:27', '9999', '2021-06-09 15:27:48', '9999');
INSERT INTO `ms_dataset` VALUES (24, 'Data Kemiskinan', 'data-kemiskinan-1623060973', 'kemiskinan', 3, 17, 'jiwa', '1', '2010', '[\"CSV\",\"Excel\",\"API\"]', '<p>Deskripsi singkat aja</p>', 'Kabupaten', 'kabupaten', 'Tahunan', '1', '2021-06-02 14:23:56', 53, '1', '2021-06-02 14:23:37', '9999', '2021-06-07 17:16:11', '9999');
INSERT INTO `ms_dataset` VALUES (25, 'Indeks Pembangunan Manusia Berdasarkan Provinsi di Indonesia', 'indeks-pembangunan-manusia-berdasarkan-provinsi-di-indonesia-1623226337', 'Indeks Pembangunan Manusia Berdasarkan Provinsi', 4, 13, 'Poin', '1', '2016', '[\"CSV\",\"Excel\",\"API\"]', '<p><span style=\"color: #616161; font-family: Lato; font-size: 14px; white-space: pre-wrap;\">Dataset ini berisi data indeks pembangunan manusia berdasarkan Provinsi di Indonesia dari tahun 2010 s.d. 2018. Dataset terkait topik kependudukan ini dihasilkan oleh Badan Pusat Statistik yang dikeluarkan dalam periode 1 tahun sekali. Penjelasan mengenai variabel di dalam dataset ini: * kode_provinsi: menyatakan kode dari setiap Provinsi di Indonesia sesuai ketentuan BPS merujuk pada aturan Peraturan Badan Pusat Statistik Nomor 3 Tahun 2019 dengan tipe data numerik. * nama_provinsi: menyatakan lingkup data berasal dari wilayah Provinsi di Indonesia sesuai ketentuan BPS merujuk pada aturan Peraturan Badan Pusat Statistik Nomor 3 Tahun 2019 dengan tipe data teks. * indeks_pembangunan_manusia : menyatakan nilai indeks pembangunan manusia berdasarkan provinsi dengan tipe data numerik. * satuan : menyatakan satuan dari pengukuran indeks pembangunan manusia berdasarkan provinsi dalam poin dengan tipe data teks. * tahun : menyatakan tahun produksi data dengan tipe data numerik.</span></p>', 'Kabupaten', 'Kabupaten Karawang', 'Tahunan', '1', '2021-06-03 16:41:56', 0, '1', '2021-06-03 15:39:36', '199108272019021003', '2021-06-09 15:12:17', '9999');
INSERT INTO `ms_dataset` VALUES (26, 'Produktivitas Sayuran dan Buah Berdasarkan Komoditi di Jawa Barat', 'produktivitas-sayuran-dan-buah-berdasarkan-komoditi-di-jawa-barat-210603043541', 'Produktivitas Sayuran dan Buah', 1, 24, 'Kuintal Per Hektar', '1', '2015', '[\"CSV\",\"Excel\",\"API\"]', '<p><span style=\"color: #616161; font-family: Lato; font-size: 14px; white-space: pre-wrap;\">Dataset ini berisi data produktivitas sayuran dan buah berdasarkan komoditi berdasarkan komoditi di Provinsi Jawa Barat tahun 2017 s.d. 2018. Dataset terkait topik Ekonomi ini dihasilkan oleh Dinas Tanaman Pangan dan Hortikultura yang dikeluarkan dalam periode 1 tahun sekali. Penjelasan mengenai variabel di dalam dataset ini: * komoditi : menyatakan komoditi dari produktivitas sayuran dan buah dengan tipe data teks. * produktivitas_buah_sayur : menyatakan produktivitas sayuran dan buah dengan tipe data numerik. * satuan : menyatakan satuan dari pengukuran produktivitas sayuran dan buah dalam kuintal per hektar dengan tipe data teks. * tahun : menyatakan tahun produksi data dengan tipe data numerik.</span></p>', 'Kabupaten', 'Kabupaten di Karawang', 'Tahunan', '1', '2021-06-03 16:36:14', 52, '1', '2021-06-03 16:35:41', '199309112019021004', '2021-06-10 14:13:15', NULL);
