DROP VIEW IF EXISTS `v_dataset`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_dataset` AS select `ms_dataset`.`id` AS `id`,`ms_dataset`.`judul` AS `judul`,`ms_dataset`.`slug` AS `slug`,`ms_dataset`.`pengukuran` AS `pengukuran`,`ms_dataset`.`id_topik` AS `id_topik`,`ms_dataset`.`id_opd` AS `id_opd`,`ms_dataset`.`satuan` AS `satuan`,`ms_dataset`.`versi` AS `versi`,`ms_dataset`.`tahun_tersedia` AS `tahun_tersedia`,`ms_dataset`.`format_tersedia` AS `format_tersedia`,`ms_dataset`.`deskripsi` AS `deskripsi`,`ms_dataset`.`tingkat_penyajian` AS `tingkat_penyajian`,`ms_dataset`.`cakupan` AS `cakupan`,`ms_dataset`.`frekuensi` AS `frekuensi`,`ms_dataset`.`views` AS `views`,`ms_dataset`.`status` AS `status`,`ms_dataset`.`publish` AS `publish`,`ms_dataset`.`published_date` AS `published_date`,`ms_dataset`.`created_at` AS `created_at`,`ms_dataset`.`created_by` AS `created_by`,`ms_dataset`.`updated_at` AS `updated_at`,`ms_dataset`.`updated_by` AS `updated_by`,`ms_topik`.`nama_topik` AS `nama_topik`,lcase(replace(replace(replace(replace(replace(replace(replace(replace(replace(replace(replace(replace(replace(replace(replace(trim(`ms_topik`.`nama_topik`),':',''),')',''),'(',''),',',''),'\\',''),'/',''),'"',''),'?',''),'\'',''),'&',''),'!',''),'.',''),' ','-'),'--','-'),'--','-')) AS `slug_topik`,`ms_topik`.`img` AS `img_topik`,`ms_opd`.`nama` AS `nama_opd`,`ms_opd`.`img` AS `img_opd`,`ms_opd`.`alias` AS `alias`,`ms_detail_dataset`.`file` AS `file`,`ms_detail_dataset`.`id` AS `id_file` from (((`ms_dataset` join `ms_topik` on(`ms_dataset`.`id_topik` = `ms_topik`.`id`)) join `ms_opd` on(`ms_dataset`.`id_opd` = `ms_opd`.`id`)) join `ms_detail_dataset` on(`ms_dataset`.`id` = `ms_detail_dataset`.`id_dataset`));

-- ----------------------------
-- View structure for v_dataset_join_opd
-- ----------------------------
DROP VIEW IF EXISTS `v_dataset_join_opd`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_dataset_join_opd` AS select `ms_opd`.`id` AS `id`,`ms_opd`.`nama` AS `nama`,count(`v_dataset`.`id`) AS `jumlah_dataset`,`ms_opd`.`img` AS `img` from (`ms_opd` left join `v_dataset` on(`ms_opd`.`id` = `v_dataset`.`id_opd`)) where `ms_opd`.`status` = '1' group by `ms_opd`.`id`;

